import { useEffect} from "react";
import { useWorkoutsContext} from "../content/WorkoutContent.js";
//匯入 WorkoutDetails 9
import WorkoutDetails from "../components/WorkoutDetails";
import WorkoutForm from "../components/Workoutform.js"; //ch10
const Home = () => {
const {workouts,dispatch} = useWorkoutsContext()
  //ch9 將所有workout顯示在home page
  useEffect(() => {
    const fetchWorkouts = async () => {
      const response = await fetch("/api/workouts");
      const json = await response.json();

      if (response.ok) {
   //更新workout
   dispatch({type:'SET_WORKOUTS',payload:json})//dispatch 格式({type:  ,  })
      }
    };
    fetchWorkouts();
  }, []); //加上[]这令 useEffect只會在第一次觸發

  return (
    <div className="home">
      <div className="workouts">
        {/* 單獨觸發workout並回傳資料名称 ch9*/}
        {workouts &&
          workouts.map((workout) => (
            <WorkoutDetails key={workout._id} workout={workout} />
          ))}
      </div>
      <WorkoutForm /> {/* ch 10 */}
    </div>
  );
};
export default Home; //令期他js可以用這function
